import { GoogleGenAI } from "@google/genai";

// DON'T DELETE THIS COMMENT
// Follow these instructions when using this blueprint:
// - Note that the newest Gemini model series is "gemini-2.5-flash" or "gemini-2.5-pro"
//   - do not change this unless explicitly requested by the user

const ai = new GoogleGenAI({ apiKey: process.env.GEMINI_API_KEY || "" });

interface DiagnosisRequest {
  symptoms: string[];
  vitalSigns?: {
    temperature?: number;
    bloodPressure?: string;
    heartRate?: number;
    respiratoryRate?: number;
    oxygenSaturation?: number;
  };
  patientAge: number;
  patientGender: string;
  patientWeight?: number;
  language: string;
}

interface DiagnosisResponse {
  primaryDiagnosis: string;
  differentialDiagnoses: Array<{
    condition: string;
    confidence: number;
    reasoning: string;
  }>;
  treatmentProtocol: {
    medications: Array<{
      name: string;
      dosage: string;
      frequency: string;
      duration: string;
    }>;
    procedures: string[];
    lifestyle: string[];
  };
  requiresReferral: boolean;
  referralReason?: string;
}

export async function generateDiagnosis(request: DiagnosisRequest): Promise<DiagnosisResponse> {
  const vitalSignsText = request.vitalSigns
    ? `
Vital Signs:
- Temperature: ${request.vitalSigns.temperature ? request.vitalSigns.temperature + "°F" : "Not provided"}
- Blood Pressure: ${request.vitalSigns.bloodPressure || "Not provided"}
- Heart Rate: ${request.vitalSigns.heartRate ? request.vitalSigns.heartRate + " bpm" : "Not provided"}
- Respiratory Rate: ${request.vitalSigns.respiratoryRate || "Not provided"}
- Oxygen Saturation: ${request.vitalSigns.oxygenSaturation ? request.vitalSigns.oxygenSaturation + "%" : "Not provided"}
`
    : "";

  const systemPrompt = `You are an experienced medical doctor working in a rural healthcare center in India. Your role is to provide differential diagnoses, treatment recommendations, and identify cases requiring specialist referral.

IMPORTANT GUIDELINES:
1. Focus on common diseases prevalent in rural India (infectious diseases, tropical diseases, nutritional deficiencies, chronic conditions)
2. Follow Indian healthcare protocols and available medications
3. Consider limited diagnostic resources in rural settings
4. Provide practical, implementable treatment plans
5. Be conservative with referrals but flag serious conditions immediately
6. Consider patient demographics and local disease patterns

RESPONSE FORMAT:
You must respond with a valid JSON object containing:
{
  "primaryDiagnosis": "Most likely diagnosis based on symptoms",
  "differentialDiagnoses": [
    {
      "condition": "Alternative diagnosis name",
      "confidence": 75,
      "reasoning": "Brief explanation why this is possible"
    }
  ],
  "treatmentProtocol": {
    "medications": [
      {
        "name": "Generic medication name commonly available in India",
        "dosage": "Specific dosage based on patient age/weight",
        "frequency": "How often to take (e.g., 'Twice daily')",
        "duration": "Treatment duration (e.g., '7 days')"
      }
    ],
    "procedures": ["Non-medication interventions"],
    "lifestyle": ["Lifestyle and dietary recommendations"]
  },
  "requiresReferral": false,
  "referralReason": "Explanation if referral is needed"
}

REFERRAL CRITERIA:
- Severe symptoms suggesting emergency
- Conditions beyond primary care scope
- Need for specialized diagnostics
- Chronic conditions requiring specialist management
- Pediatric/geriatric cases with complications`;

  const userPrompt = `Patient Demographics:
- Age: ${request.patientAge} years
- Gender: ${request.patientGender}
- Weight: ${request.patientWeight ? request.patientWeight + " kg" : "Not provided"}

Presenting Symptoms:
${request.symptoms.map((s) => `- ${s}`).join("\n")}

${vitalSignsText}

Please provide a comprehensive medical assessment with differential diagnoses, treatment recommendations following Indian healthcare protocols, and specialist referral guidance if needed.`;

  try {
    const response = await ai.models.generateContent({
      model: "gemini-2.5-pro",
      config: {
        systemInstruction: systemPrompt,
        responseMimeType: "application/json",
        responseSchema: {
          type: "object",
          properties: {
            primaryDiagnosis: { type: "string" },
            differentialDiagnoses: {
              type: "array",
              items: {
                type: "object",
                properties: {
                  condition: { type: "string" },
                  confidence: { type: "number" },
                  reasoning: { type: "string" },
                },
                required: ["condition", "confidence", "reasoning"],
              },
            },
            treatmentProtocol: {
              type: "object",
              properties: {
                medications: {
                  type: "array",
                  items: {
                    type: "object",
                    properties: {
                      name: { type: "string" },
                      dosage: { type: "string" },
                      frequency: { type: "string" },
                      duration: { type: "string" },
                    },
                    required: ["name", "dosage", "frequency", "duration"],
                  },
                },
                procedures: {
                  type: "array",
                  items: { type: "string" },
                },
                lifestyle: {
                  type: "array",
                  items: { type: "string" },
                },
              },
              required: ["medications", "procedures", "lifestyle"],
            },
            requiresReferral: { type: "boolean" },
            referralReason: { type: "string" },
          },
          required: ["primaryDiagnosis", "differentialDiagnoses", "treatmentProtocol", "requiresReferral"],
        },
      },
      contents: userPrompt,
    });

    const rawJson = response.text;

    if (!rawJson) {
      throw new Error("Empty response from Gemini");
    }

    const diagnosis: DiagnosisResponse = JSON.parse(rawJson);
    return diagnosis;
  } catch (error) {
    console.error("Gemini diagnosis error:", error);
    throw new Error(`Failed to generate diagnosis: ${error}`);
  }
}

export async function checkDrugInteractions(medications: string[], patientAge: number, patientWeight?: number): Promise<{
  hasInteractions: boolean;
  interactions: Array<{
    medications: string[];
    severity: string;
    description: string;
  }>;
  dosageAdjustments: Array<{
    medication: string;
    recommendedDosage: string;
    reason: string;
  }>;
}> {
  const systemPrompt = `You are a clinical pharmacist specializing in drug interactions and dosage calculations for rural healthcare settings in India.

Analyze the provided medications for:
1. Drug-drug interactions
2. Age-appropriate dosing
3. Weight-based dosage adjustments
4. Common contraindications

Focus on medications commonly available in Indian healthcare centers.

Respond with valid JSON:
{
  "hasInteractions": boolean,
  "interactions": [
    {
      "medications": ["Drug A", "Drug B"],
      "severity": "Minor|Moderate|Severe",
      "description": "Explanation of interaction"
    }
  ],
  "dosageAdjustments": [
    {
      "medication": "Drug name",
      "recommendedDosage": "Adjusted dosage",
      "reason": "Explanation for adjustment"
    }
  ]
}`;

  const userPrompt = `Patient Information:
- Age: ${patientAge} years
- Weight: ${patientWeight ? patientWeight + " kg" : "Not provided"}

Medications to analyze:
${medications.map((m) => `- ${m}`).join("\n")}

Please check for drug interactions and provide dosage recommendations.`;

  try {
    const response = await ai.models.generateContent({
      model: "gemini-2.5-flash",
      config: {
        systemInstruction: systemPrompt,
        responseMimeType: "application/json",
        responseSchema: {
          type: "object",
          properties: {
            hasInteractions: { type: "boolean" },
            interactions: {
              type: "array",
              items: {
                type: "object",
                properties: {
                  medications: {
                    type: "array",
                    items: { type: "string" },
                  },
                  severity: { type: "string" },
                  description: { type: "string" },
                },
                required: ["medications", "severity", "description"],
              },
            },
            dosageAdjustments: {
              type: "array",
              items: {
                type: "object",
                properties: {
                  medication: { type: "string" },
                  recommendedDosage: { type: "string" },
                  reason: { type: "string" },
                },
                required: ["medication", "recommendedDosage", "reason"],
              },
            },
          },
          required: ["hasInteractions", "interactions", "dosageAdjustments"],
        },
      },
      contents: userPrompt,
    });

    const rawJson = response.text;

    if (!rawJson) {
      throw new Error("Empty response from Gemini");
    }

    return JSON.parse(rawJson);
  } catch (error) {
    console.error("Drug interaction check error:", error);
    throw new Error(`Failed to check drug interactions: ${error}`);
  }
}

