import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { generateDiagnosis, checkDrugInteractions } from "./gemini";
import { insertPatientSchema, symptomAnalysisSchema, drugInteractionSchema } from "@shared/schema";
import { z } from "zod";

export async function registerRoutes(app: Express): Promise<Server> {
  // Patient routes
  app.get("/api/patients", async (_req, res) => {
    try {
      const patients = await storage.getAllPatients();
      res.json(patients);
    } catch (error) {
      console.error("Error fetching patients:", error);
      res.status(500).json({ message: "Failed to fetch patients" });
    }
  });

  app.get("/api/patients/:id", async (req, res) => {
    try {
      const patient = await storage.getPatient(req.params.id);
      if (!patient) {
        return res.status(404).json({ message: "Patient not found" });
      }
      res.json(patient);
    } catch (error) {
      console.error("Error fetching patient:", error);
      res.status(500).json({ message: "Failed to fetch patient" });
    }
  });

  app.post("/api/patients", async (req, res) => {
    try {
      const validatedData = insertPatientSchema.parse(req.body);
      const patient = await storage.createPatient(validatedData);
      res.status(201).json(patient);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid patient data", errors: error.errors });
      }
      console.error("Error creating patient:", error);
      res.status(500).json({ message: "Failed to create patient" });
    }
  });

  // Diagnosis routes
  app.get("/api/diagnoses", async (_req, res) => {
    try {
      const diagnoses = await storage.getAllDiagnoses();
      res.json(diagnoses);
    } catch (error) {
      console.error("Error fetching diagnoses:", error);
      res.status(500).json({ message: "Failed to fetch diagnoses" });
    }
  });

  app.get("/api/diagnoses/:id", async (req, res) => {
    try {
      const diagnosis = await storage.getDiagnosis(req.params.id);
      if (!diagnosis) {
        return res.status(404).json({ message: "Diagnosis not found" });
      }
      res.json(diagnosis);
    } catch (error) {
      console.error("Error fetching diagnosis:", error);
      res.status(500).json({ message: "Failed to fetch diagnosis" });
    }
  });

  app.get("/api/patients/:id/diagnoses", async (req, res) => {
    try {
      const diagnoses = await storage.getDiagnosesByPatient(req.params.id);
      res.json(diagnoses);
    } catch (error) {
      console.error("Error fetching patient diagnoses:", error);
      res.status(500).json({ message: "Failed to fetch patient diagnoses" });
    }
  });

  // Main diagnosis endpoint - analyzes symptoms and generates diagnosis
  app.post("/api/diagnose", async (req, res) => {
    try {
      const validatedData = symptomAnalysisSchema.parse(req.body);

      // Generate diagnosis using Gemini AI
      const aiDiagnosis = await generateDiagnosis({
        symptoms: validatedData.symptoms,
        vitalSigns: validatedData.vitalSigns,
        patientAge: validatedData.patientAge,
        patientGender: validatedData.patientGender,
        patientWeight: validatedData.patientWeight,
        language: validatedData.language,
      });

      // Store the diagnosis
      const diagnosis = await storage.createDiagnosis({
        patientId: validatedData.patientId,
        symptoms: validatedData.symptoms,
        vitalSigns: validatedData.vitalSigns,
        primaryDiagnosis: aiDiagnosis.primaryDiagnosis,
        differentialDiagnoses: aiDiagnosis.differentialDiagnoses,
        treatmentProtocol: aiDiagnosis.treatmentProtocol,
        requiresReferral: aiDiagnosis.requiresReferral,
        referralReason: aiDiagnosis.referralReason,
        language: validatedData.language,
      });

      // Add to knowledge base for learning (anonymized)
      const ageGroup = validatedData.patientAge < 18 ? "child" : 
                       validatedData.patientAge < 60 ? "adult" : "senior";

      await storage.addKnowledgeEntry({
        symptoms: validatedData.symptoms,
        ageGroup,
        gender: validatedData.patientGender,
        diagnosis: aiDiagnosis.primaryDiagnosis,
        confidence: aiDiagnosis.differentialDiagnoses[0]?.confidence || 100,
        outcome: null,
      });

      res.status(201).json(diagnosis);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid diagnosis data", errors: error.errors });
      }
      console.error("Error generating diagnosis:", error);
      res.status(500).json({ 
        message: "Failed to generate diagnosis. Please check if Gemini API key is configured correctly.",
        error: error instanceof Error ? error.message : "Unknown error"
      });
    }
  });

  // Drug interaction check endpoint
  app.post("/api/drug-interactions", async (req, res) => {
    try {
      const validatedData = drugInteractionSchema.parse(req.body);

      const interactionResult = await checkDrugInteractions(
        validatedData.medications,
        validatedData.patientAge,
        validatedData.patientWeight
      );

      res.json(interactionResult);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid request data", errors: error.errors });
      }
      console.error("Error checking drug interactions:", error);
      res.status(500).json({ 
        message: "Failed to check drug interactions",
        error: error instanceof Error ? error.message : "Unknown error"
      });
    }
  });

  // Knowledge base search endpoint (for finding similar past cases)
  app.post("/api/knowledge/search", async (req, res) => {
    try {
      const { symptoms, ageGroup, gender } = req.body;

      if (!symptoms || !Array.isArray(symptoms) || symptoms.length === 0) {
        return res.status(400).json({ message: "Symptoms are required" });
      }

      const results = await storage.searchKnowledge(symptoms, ageGroup, gender);
      res.json(results);
    } catch (error) {
      console.error("Error searching knowledge base:", error);
      res.status(500).json({ message: "Failed to search knowledge base" });
    }
  });

  const httpServer = createServer(app);

  return httpServer;
}

server/storage.ts.content:
import { type Patient, type InsertPatient, type Diagnosis, type InsertDiagnosis, type KnowledgeBase } from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  // Patient operations
  getPatient(id: string): Promise<Patient | undefined>;
  getAllPatients(): Promise<Patient[]>;
  createPatient(patient: InsertPatient): Promise<Patient>;

  // Diagnosis operations
  getDiagnosis(id: string): Promise<Diagnosis | undefined>;
  getDiagnosesByPatient(patientId: string): Promise<Diagnosis[]>;
  getAllDiagnoses(): Promise<Diagnosis[]>;
  createDiagnosis(diagnosis: InsertDiagnosis): Promise<Diagnosis>;

  // Knowledge base operations
  addKnowledgeEntry(entry: Omit<KnowledgeBase, "id" | "createdAt">): Promise<KnowledgeBase>;
  searchKnowledge(symptoms: string[], ageGroup: string, gender: string): Promise<KnowledgeBase[]>;
}

export class MemStorage implements IStorage {
  private patients: Map<string, Patient>;
  private diagnoses: Map<string, Diagnosis>;
  private knowledgeBase: Map<string, KnowledgeBase>;

  constructor() {
    this.patients = new Map();
    this.diagnoses = new Map();
    this.knowledgeBase = new Map();

    // Initialize with some sample patients for demonstration
    this.initializeSampleData();
  }

  private initializeSampleData() {
    const samplePatients = [
      {
        id: randomUUID(),
        name: "Rajesh Kumar",
        age: 45,
        gender: "male",
        weight: 72,
        contactNumber: "+91 98765 43210",
        address: "Village Rampur, District Ghaziabad",
        createdAt: new Date(),
      },
      {
        id: randomUUID(),
        name: "Priya Sharma",
        age: 32,
        gender: "female",
        weight: 58,
        contactNumber: "+91 98765 43211",
        address: "Village Kamlapur, District Meerut",
        createdAt: new Date(),
      },
      {
        id: randomUUID(),
        name: "Amit Patel",
        age: 28,
        gender: "male",
        weight: 65,
        contactNumber: "+91 98765 43212",
        address: "Village Badarpur, District Noida",
        createdAt: new Date(),
      },
    ];

    samplePatients.forEach((patient) => {
      this.patients.set(patient.id, patient);
    });
  }

  // Patient operations
  async getPatient(id: string): Promise<Patient | undefined> {
    return this.patients.get(id);
  }

  async getAllPatients(): Promise<Patient[]> {
    return Array.from(this.patients.values()).sort(
      (a, b) => b.createdAt.getTime() - a.createdAt.getTime()
    );
  }

  async createPatient(insertPatient: InsertPatient): Promise<Patient> {
    const id = randomUUID();
    const patient: Patient = {
      ...insertPatient,
      id,
      createdAt: new Date(),
    };
    this.patients.set(id, patient);
    return patient;
  }

  // Diagnosis operations
  async getDiagnosis(id: string): Promise<Diagnosis | undefined> {
    return this.diagnoses.get(id);
  }

  async getDiagnosesByPatient(patientId: string): Promise<Diagnosis[]> {
    return Array.from(this.diagnoses.values())
      .filter((d) => d.patientId === patientId)
      .sort((a, b) => b.createdAt.getTime() - a.createdAt.getTime());
  }

  async getAllDiagnoses(): Promise<Diagnosis[]> {
    return Array.from(this.diagnoses.values()).sort(
      (a, b) => b.createdAt.getTime() - a.createdAt.getTime()
    );
  }

  async createDiagnosis(insertDiagnosis: InsertDiagnosis): Promise<Diagnosis> {
    const id = randomUUID();
    const diagnosis: Diagnosis = {
      ...insertDiagnosis,
      id,
      createdAt: new Date(),
      vitalSigns: insertDiagnosis.vitalSigns || null,
      treatmentProtocol: insertDiagnosis.treatmentProtocol || null,
      requiresReferral: insertDiagnosis.requiresReferral || false,
      referralReason: insertDiagnosis.referralReason || null,
    };
    this.diagnoses.set(id, diagnosis);
    return diagnosis;
  }

  // Knowledge base operations
  async addKnowledgeEntry(entry: Omit<KnowledgeBase, "id" | "createdAt">): Promise<KnowledgeBase> {
    const id = randomUUID();
    const knowledgeEntry: KnowledgeBase = {
      ...entry,
      id,
      createdAt: new Date(),
      outcome: entry.outcome || null,
    };
    this.knowledgeBase.set(id, knowledgeEntry);
    return knowledgeEntry;
  }

  async searchKnowledge(symptoms: string[], ageGroup: string, gender: string): Promise<KnowledgeBase[]> {
    return Array.from(this.knowledgeBase.values())
      .filter((entry) => {
        const symptomsMatch = symptoms.some((symptom) =>
          entry.symptoms.includes(symptom)
        );
        const ageGroupMatch = entry.ageGroup === ageGroup;
        const genderMatch = entry.gender === gender || entry.gender === "other";
        return symptomsMatch && ageGroupMatch && genderMatch;
      })
      .sort((a, b) => b.confidence - a.confidence);
  }
}

export const storage = new MemStorage();

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

